import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../client';
import './PostDetail.css';

const PostDetail = () => {
    const { id } = useParams(); 
    const [post, setPost] = useState(null);

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const { data, error } = await supabase
                    .from('Final')
                    .select()
                    .eq('id', id);

                if (error) {
                    throw error;
                }

                if (data && data.length > 0) {
                    setPost(data[0]);
                } else {
                    throw new Error('Post not found');
                }
            } catch (error) {
                console.error('Error fetching post:', error.message);
                // Handle error
            }
        };

        fetchPost();
    }, [id]);

    return (
        <div className="PostDetail">
            {post ? (
                <div>
                    <h2 className="plate-details">Plate Details</h2>
                    <p className="name">Plate's Name{post.name}</p>
                    <p className="nationality"> Nationality{post.nationality}</p>
                    <p className="category">Category{post.category}</p>
                    <p className="description">Descriptio{post.description}</p>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default PostDetail;
