import React from 'react';
import { useParams } from 'react-router-dom';
import './EditPost.css'
import { supabase } from '../client'

const EditPost = ({data}) => {

    const {id} = useParams();
    const [post, setPost] = useState({id: null, name: "", nationality: "", description: ""});

    const handleChange = (event) => {
        const {name, value} = event.target;
        setPost( (prev) => {
            return {
                ...prev,
                [name]:value,
            }
        })
    }
    const updatePost = async (event) => {
        event.preventDefault();

        await supabase
        .from('Final')
        .update({name: post.name, nationality: post.nationality, description: post.description})
        .eq('id', id)

        window.location = "/";
    }

    const deletePost = async (event) => {
        event.preventDefault();

            await supabase
                .from('Final')
                .delete()
                .match({id: id});

            window.location = "http://localhost:3000/";
    }


    return (
        <div>
            <form>
                <label for="title">Name</label> <br />
                <input type="text" id="name" name="name" value={post.name} onChange={handleChange} /><br />
                <br/>

                <label for="author">Nationality</label><br />
                <input type="text" id="nationality" name="nationality" value={post.nationality} onChange={handleChange} /><br />
                <br/>

                <label for="description">Description</label><br />
                <textarea rows="5" cols="50" id="description" value={post.description} onChange={handleChange} >
                </textarea>
                <br/>
                <input type="submit" value="Submit" />
                <button className="deleteButton" onClick={deletePost}>Delete</button>
            </form>
        </div>
    )
}

export default EditPost
